    def check_data(self):
        if len(self.lineEdit.text())<1:
            self.name_error=QMessageBox.critical(self,"Error!!","Enter must name!!")
        elif len(self.lineEdit_2.text())<1:
            self.sur_error=QMessageBox.critical(self,"Error!!","Enter must username!!")
        elif not re.match(r"[^@ \t\r\n]+@[^@ \t\r\n]+\.[^@ \t\r\n]+", self.lineEdit_3.text()):
            self.emeil_edit_error=QMessageBox.critical(self,"Error","Please enter the correct email address!!!")
        elif len(self.lineEdit_4.text())<12:
            self.phone=QMessageBox.critical(self,"Error!!!","Error phone number !")
        elif len(self.lineEdit_5.text())<8:
            self.pass_err=QMessageBox.critical(self,"Error","password must be at least 8 characters long")
        else:
            name=self.lineEdit.text()
            surname=self.lineEdit_2.text()
            pasword=self.lineEdit_4.text()
            if name:
                if surname:
                    if pasword:
                        cur.execute(sql.date.add_pupl(),(name,surname,pasword))
                        cn.commit()
            self.signedup = QMessageBox.about(self, "Done.", "âœ… You are registered succesfully!!!")
            self.s_up.sign_up_signal.emit()
            self.hide()
            print("Finish")
